import React, { useState, useEffect, useRef } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import { FaArrowLeft, FaPaperPlane, FaPaperclip, FaUser, FaCircle, FaDownload, FaTrash } from 'react-icons/fa';
import { useAuth } from '../../context/AuthContext';
import { useSocket } from '../../context/SocketContext';
import { apiHelpers, uploadFileWithProgress, utils } from '../../utils/api';
import { toast } from 'react-hot-toast';
import moment from 'moment';

const ChatPage = () => {
  const { chatId } = useParams();
  const navigate = useNavigate();
  const { user } = useAuth();
  const { 
    socket, 
    connected, 
    messages, 
    joinChat, 
    leaveChat, 
    sendMessage, 
    sendTyping, 
    sendStopTyping, 
    markMessagesAsRead,
    isUserOnline,
    getTypingUsers,
    clearMessages
  } = useSocket();

  const [chatData, setChatData] = useState(null);
  const [messageInput, setMessageInput] = useState('');
  const [chatMessages, setChatMessages] = useState([]);
  const [loading, setLoading] = useState(true);
  const [sending, setSending] = useState(false);
  const [typing, setTyping] = useState(false);
  const [fileUploading, setFileUploading] = useState(false);
  const [uploadProgress, setUploadProgress] = useState(0);

  const messagesEndRef = useRef(null);
  const fileInputRef = useRef(null);
  const typingTimeoutRef = useRef(null);

  // Get other participant info
  const otherParticipant = chatData?.otherParticipants?.[0];
  const isOtherUserOnline = otherParticipant && isUserOnline(otherParticipant._id);
  const typingUsers = getTypingUsers(chatId);

  useEffect(() => {
    if (chatId) {
      loadChatData();
      loadMessages();
      joinChat(chatId);
      clearMessages(); // Clear previous chat messages
    }

    return () => {
      if (chatId) {
        leaveChat(chatId);
        clearMessages();
      }
    };
  }, [chatId]);

  // Update local messages when socket messages change
  useEffect(() => {
    setChatMessages(messages);
  }, [messages]);

  // Auto-scroll to bottom when new messages arrive
  useEffect(() => {
    scrollToBottom();
  }, [chatMessages]);

  // Mark messages as read when they come into view
  useEffect(() => {
    if (chatMessages.length > 0) {
      const unreadMessages = chatMessages
        .filter(msg => msg.receiver === user._id && !msg.isRead)
        .map(msg => msg._id);
      
      if (unreadMessages.length > 0) {
        markMessagesAsRead(chatId, unreadMessages);
      }
    }
  }, [chatMessages, user._id, chatId]);

  const loadChatData = async () => {
    try {
      // Get chat details including participants
      const response = await apiHelpers.getUserChats();
      if (response.data.success) {
        const currentChat = response.data.data.chats.find(chat => chat._id === chatId);
        if (currentChat) {
          setChatData(currentChat);
          console.log('✅ Chat data loaded:', currentChat);
        }
      }
    } catch (error) {
      console.error('Load chat data error:', error);
      toast.error('Failed to load chat data');
    }
  };

  const loadMessages = async () => {
    try {
      setLoading(true);
      const response = await apiHelpers.getChatMessages(chatId);
      if (response.data.success) {
        const messages = response.data.data.messages;
        setChatMessages(messages);
        
        // If no chat data yet, extract it from messages
        if (!chatData && messages.length > 0) {
          const firstMessage = messages[0];
          const participants = [];
          
          // Get unique participants from messages
          const senderIds = [...new Set(messages.map(m => m.sender._id))];
          const receiverIds = [...new Set(messages.map(m => m.receiver))];
          const allParticipantIds = [...new Set([...senderIds, ...receiverIds])];
          
          // Find the other participant (not current user)
          const otherParticipantId = allParticipantIds.find(id => id !== user._id);
          const otherParticipant = messages.find(m => m.sender._id === otherParticipantId)?.sender;
          
          if (otherParticipant) {
            participants.push(otherParticipant);
          }
          
          setChatData({
            _id: chatId,
            participants: [otherParticipant].filter(Boolean),
            otherParticipants: [otherParticipant].filter(Boolean),
            chatType: 'private'
          });
          
          console.log('✅ Chat data extracted from messages:', {
            otherParticipant,
            participants
          });
        }
      }
    } catch (error) {
      console.error('Load messages error:', error);
      toast.error('Failed to load messages');
      navigate('/dashboard');
    } finally {
      setLoading(false);
    }
  };

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  };

  const handleSendMessage = async (e) => {
    e.preventDefault();
    
    if (!messageInput.trim() || !connected) {
      console.log('Cannot send message:', {
        hasInput: !!messageInput.trim(),
        connected,
        chatId
      });
      return;
    }

    // Get receiver ID from chat data or messages
    let receiverId = null;
    if (otherParticipant) {
      receiverId = otherParticipant._id;
    } else if (chatMessages.length > 0) {
      // Get receiver from existing messages
      const lastMessage = chatMessages[chatMessages.length - 1];
      receiverId = lastMessage.sender._id === user._id ? lastMessage.receiver : lastMessage.sender._id;
    }

    if (!receiverId) {
      toast.error('Cannot identify message recipient');
      return;
    }

    try {
      setSending(true);
      
      const messageData = {
        chatId,
        receiverId,
        content: messageInput.trim(),
        messageType: 'text'
      };

      console.log('🔍 Sending message:', messageData);

      // Send via API
      const response = await apiHelpers.sendMessage(messageData);
      console.log('✅ Message sent via API:', response.data);

      if (response.data.success) {
        // Add message to local state immediately
        const newMessage = response.data.data.message;
        setChatMessages(prev => [...prev, newMessage]);
        
        // Also send via socket for real-time delivery to other user
        sendMessage(messageData);
        
        setMessageInput('');
        stopTyping();
        toast.success('Message sent!');
      }
    } catch (error) {
      console.error('Send message error:', error);
      toast.error('Failed to send message: ' + (error.response?.data?.message || error.message));
    } finally {
      setSending(false);
    }
  };

  const handleFileUpload = async (file) => {
    if (!file || !chatMessages.length && !otherParticipant) return;

    const validation = utils.validateFile(file);
    if (!validation.valid) {
      toast.error(validation.error);
      return;
    }

    try {
      setFileUploading(true);
      setUploadProgress(0);

      console.log('🔍 Uploading file:', {
        name: file.name,
        type: file.type,
        size: file.size
      });

      // Create form data
      const formData = new FormData();
      formData.append('file', file);

      // Determine upload endpoint based on file type
      const fileType = utils.getFileType(file.type);
      
      // Use the generic file upload endpoint
      const response = await uploadFileWithProgress(formData, 'file', setUploadProgress);

      console.log('✅ File uploaded:', response.data);

      if (response.data.success) {
        const { fileUrl, fileName, fileSize, messageType } = response.data.data;
        
        // Get receiver ID
        let receiverId = null;
        if (otherParticipant) {
          receiverId = otherParticipant._id;
        } else if (chatMessages.length > 0) {
          const lastMessage = chatMessages[chatMessages.length - 1];
          receiverId = lastMessage.sender._id === user._id ? lastMessage.receiver : lastMessage.sender._id;
        }

        if (receiverId) {
          const messageData = {
            chatId,
            receiverId,
            messageType,
            fileUrl,
            fileName,
            fileSize
          };

          // Send file message via API
          const messageResponse = await apiHelpers.sendMessage(messageData);
          if (messageResponse.data.success) {
            const newMessage = messageResponse.data.data.message;
            
            // Add message to local state immediately
            setChatMessages(prev => [...prev, newMessage]);
            
            // Also send via socket for real-time delivery to other user
            sendMessage(messageData);
            
            toast.success(`${messageType.charAt(0).toUpperCase() + messageType.slice(1)} sent successfully!`);
            
            // Scroll to bottom to show the new message
            setTimeout(() => scrollToBottom(), 100);
          }
        }
      }
    } catch (error) {
      console.error('File upload error:', error);
      toast.error('Failed to upload file: ' + (error.response?.data?.message || error.message));
    } finally {
      setFileUploading(false);
      setUploadProgress(0);
    }
  };

  const handleInputChange = (e) => {
    setMessageInput(e.target.value);
    
    if (!typing && otherParticipant) {
      setTyping(true);
      sendTyping(chatId, otherParticipant._id);
    }

    // Clear existing timeout
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }

    // Set new timeout to stop typing
    typingTimeoutRef.current = setTimeout(stopTyping, 2000);
  };

  const stopTyping = () => {
    if (typing && otherParticipant) {
      setTyping(false);
      sendStopTyping(chatId, otherParticipant._id);
    }
    if (typingTimeoutRef.current) {
      clearTimeout(typingTimeoutRef.current);
    }
  };

  const handleDeleteMessage = async (messageId) => {
    try {
      await apiHelpers.deleteMessage(messageId);
      setChatMessages(prev => prev.filter(msg => msg._id !== messageId));
      toast.success('Message deleted');
    } catch (error) {
      console.error('Delete message error:', error);
      toast.error('Failed to delete message');
    }
  };

  const formatMessageTime = (timestamp) => {
    return moment(timestamp).format('HH:mm');
  };

  const formatFileSize = (bytes) => {
    return utils.formatFileSize(bytes);
  };

  const renderMessage = (message) => {
    const isOwnMessage = message.sender._id === user._id;
    const showSender = !isOwnMessage;

    return (
      <div
        key={message._id}
        className={`flex ${isOwnMessage ? 'justify-end' : 'justify-start'} mb-4`}
      >
        <div className={`max-w-xs lg:max-w-md ${isOwnMessage ? 'order-2' : 'order-1'}`}>
          {showSender && (
            <div className="text-xs text-gray-500 mb-1 ml-2">
              {message.sender.name}
            </div>
          )}
          
          <div
            className={`px-4 py-2 rounded-lg ${
              isOwnMessage
                ? 'bg-blue-600 text-white rounded-br-none'
                : 'bg-gray-200 text-gray-900 rounded-bl-none'
            }`}
          >
            {/* Render different message types */}
            {message.messageType === 'text' ? (
              <p className="text-sm">{message.content}</p>
            ) : message.messageType === 'image' ? (
              <div className="space-y-2">
                <div className="relative">
                  <img
                    src={message.fileUrl}
                    alt={message.fileName || 'Image'}
                    className="max-w-full h-auto rounded-lg cursor-pointer hover:opacity-90 transition-opacity block"
                    style={{ maxHeight: '300px', maxWidth: '250px' }}
                    onClick={() => window.open(message.fileUrl, '_blank')}
                    onLoad={() => scrollToBottom()}
                    onError={(e) => {
                      e.target.style.display = 'none';
                      e.target.nextSibling.style.display = 'block';
                    }}
                  />
                  {/* Error fallback */}
                  <div 
                    className="hidden p-4 bg-gray-100 bg-opacity-20 rounded-lg text-center"
                    style={{ maxWidth: '250px' }}
                  >
                    <FaPaperclip className="h-6 w-6 mx-auto mb-2 opacity-50" />
                    <p className="text-xs opacity-75">Image failed to load</p>
                    <a
                      href={message.fileUrl}
                      target="_blank"
                      rel="noopener noreferrer"
                      className="text-xs underline opacity-75 hover:opacity-100"
                    >
                      View Original
                    </a>
                  </div>
                </div>
                {message.fileName && (
                  <p className="text-xs opacity-75">{message.fileName}</p>
                )}
              </div>
            ) : message.messageType === 'video' ? (
              <div className="space-y-2">
                <video
                  controls
                  className="max-w-full h-auto rounded-lg"
                  style={{ maxHeight: '300px', maxWidth: '250px' }}
                  onLoadedData={() => scrollToBottom()}
                >
                  <source src={message.fileUrl} type="video/mp4" />
                  Your browser does not support the video tag.
                </video>
                {message.fileName && (
                  <p className="text-xs opacity-75">{message.fileName}</p>
                )}
              </div>
            ) : (
              /* Document or other file types */
              <div className="space-y-2">
                <div className="flex items-center space-x-2 p-2 bg-gray-100 bg-opacity-20 rounded">
                  <FaPaperclip className="h-4 w-4" />
                  <div className="flex-1 min-w-0">
                    <p className="text-sm font-medium truncate">{message.fileName}</p>
                    <p className="text-xs opacity-75">
                      {formatFileSize(message.fileSize)} • {message.messageType}
                    </p>
                  </div>
                  <a
                    href={message.fileUrl}
                    target="_blank"
                    rel="noopener noreferrer"
                    className="text-xs underline opacity-75 hover:opacity-100 flex items-center space-x-1"
                  >
                    <FaDownload className="h-3 w-3" />
                    <span>Download</span>
                  </a>
                </div>
              </div>
            )}
            
            <div className="flex items-center justify-between mt-2">
              <span className="text-xs opacity-75">
                {formatMessageTime(message.createdAt)}
              </span>
              
              {isOwnMessage && (
                <div className="flex items-center space-x-2">
                  {message.isRead && (
                    <span className="text-xs opacity-75">Read</span>
                  )}
                  <button
                    onClick={() => handleDeleteMessage(message._id)}
                    className="text-xs opacity-75 hover:opacity-100"
                  >
                    <FaTrash className="h-3 w-3" />
                  </button>
                </div>
              )}
            </div>
          </div>
        </div>
      </div>
    );
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading chat...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50 flex flex-col">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200 px-4 py-3">
        <div className="flex items-center justify-between">
          <div className="flex items-center space-x-4">
            <button
              onClick={() => navigate('/dashboard')}
              className="text-gray-600 hover:text-gray-900 transition-colors"
            >
              <FaArrowLeft className="h-5 w-5" />
            </button>
            
            <div className="flex items-center space-x-3">
              <div className="relative">
                <div className="w-10 h-10 bg-gray-300 rounded-full flex items-center justify-center">
                  <FaUser className="h-5 w-5 text-gray-600" />
                </div>
                {isOtherUserOnline && (
                  <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                )}
              </div>
              
              <div>
                <h3 className="font-medium text-gray-900">
                  {otherParticipant?.name || 'Unknown User'}
                </h3>
                <p className="text-sm text-gray-500 flex items-center">
                  <FaCircle className={`h-2 w-2 mr-1 ${isOtherUserOnline ? 'text-green-500' : 'text-gray-400'}`} />
                  {isOtherUserOnline ? 'Online' : 'Offline'}
                </p>
              </div>
            </div>
          </div>

          <div className="flex items-center space-x-2">
            <div className={`w-3 h-3 rounded-full ${connected ? 'bg-green-500' : 'bg-red-500'}`}></div>
            <span className="text-sm text-gray-600">
              {connected ? 'Connected' : 'Disconnected'}
            </span>
          </div>
        </div>
      </header>

      {/* Messages Area */}
      <div className="flex-1 overflow-y-auto p-4">
        <div className="max-w-4xl mx-auto">
          {chatMessages.length > 0 ? (
            <>
              {chatMessages.map(renderMessage)}
              
              {/* Typing Indicator */}
              {typingUsers.length > 0 && (
                <div className="flex justify-start mb-4">
                  <div className="bg-gray-200 rounded-lg px-4 py-2">
                    <div className="flex space-x-1">
                      <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce"></div>
                      <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.1s' }}></div>
                      <div className="w-2 h-2 bg-gray-500 rounded-full animate-bounce" style={{ animationDelay: '0.2s' }}></div>
                    </div>
                  </div>
                </div>
              )}
            </>
          ) : (
            <div className="text-center py-8">
              <div className="text-gray-400 mb-4">
                <FaUser className="h-12 w-12 mx-auto" />
              </div>
              <h3 className="text-lg font-medium text-gray-900 mb-2">Start a conversation</h3>
              <p className="text-gray-500">Send a message to get the conversation started!</p>
            </div>
          )}
          
          <div ref={messagesEndRef} />
        </div>
      </div>

      {/* File Upload Progress */}
      {fileUploading && (
        <div className="px-4 py-2 bg-blue-50 border-t border-blue-200">
          <div className="max-w-4xl mx-auto">
            <div className="flex items-center space-x-3">
              <div className="flex-1">
                <div className="text-sm text-blue-700 mb-1">Uploading file...</div>
                <div className="w-full bg-blue-200 rounded-full h-2">
                  <div 
                    className="bg-blue-600 h-2 rounded-full transition-all duration-300"
                    style={{ width: `${uploadProgress}%` }}
                  ></div>
                </div>
              </div>
              <span className="text-sm text-blue-700">{uploadProgress}%</span>
            </div>
          </div>
        </div>
      )}

      {/* Message Input */}
      <div className="bg-white border-t border-gray-200 px-4 py-3">
        <form onSubmit={handleSendMessage} className="max-w-4xl mx-auto">
          <div className="flex items-center space-x-3">
            <button
              type="button"
              onClick={() => fileInputRef.current?.click()}
              disabled={fileUploading}
              className="text-gray-500 hover:text-gray-700 transition-colors disabled:opacity-50"
            >
              <FaPaperclip className="h-5 w-5" />
            </button>
            
            <div className="flex-1">
              <input
                type="text"
                value={messageInput}
                onChange={handleInputChange}
                onBlur={stopTyping}
                placeholder={connected ? "Type a message..." : "Connecting..."}
                className="w-full px-4 py-2 border border-gray-300 rounded-full focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                disabled={!connected || sending}
              />
            </div>
            
            <button
              type="submit"
              disabled={!messageInput.trim() || !connected || sending}
              className="bg-blue-600 text-white p-3 rounded-full hover:bg-blue-700 transition-colors disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center min-w-[48px] min-h-[48px]"
              title={
                !connected ? "Not connected" :
                !messageInput.trim() ? "Type a message" :
                sending ? "Sending..." : "Send message"
              }
            >
              {sending ? (
                <div className="animate-spin rounded-full h-5 w-5 border-b-2 border-white"></div>
              ) : (
                <FaPaperPlane className="h-5 w-5" />
              )}
            </button>
          </div>
          
          {/* Debug info */}
          {process.env.NODE_ENV === 'development' && (
            <div className="text-xs text-gray-500 mt-1">
              Connected: {connected ? '✅' : '❌'} | 
              Has Input: {messageInput.trim() ? '✅' : '❌'} | 
              Chat ID: {chatId ? '✅' : '❌'} | 
              Sending: {sending ? '🔄' : '✅'} |
              Other User: {otherParticipant?.name || 'Loading...'}
            </div>
          )}
        </form>
        
        <input
          ref={fileInputRef}
          type="file"
          className="hidden"
          accept="image/*,video/*,.pdf,.doc,.docx,.txt"
          onChange={(e) => {
            const file = e.target.files?.[0];
            if (file) {
              handleFileUpload(file);
              e.target.value = ''; // Reset input
            }
          }}
        />
      </div>
    </div>
  );
};

export default ChatPage;
