import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSearch, FaPlus, FaSignOutAlt, FaUser, FaComments, FaUsers, FaCircle } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import { useSocket } from '../context/SocketContext';
import { apiHelpers } from '../utils/api';
import { toast } from 'react-hot-toast';
import moment from 'moment';

const Dashboard = () => {
  const [chats, setChats] = useState([]);
  const [users, setUsers] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);
  const [showUserSearch, setShowUserSearch] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  
  const { user, logout } = useAuth();
  const { connected, onlineUsers, isUserOnline } = useSocket();
  const navigate = useNavigate();

  // Load user chats on component mount
  useEffect(() => {
    loadChats();
  }, []);

  // Search users when query changes
  useEffect(() => {
    if (searchQuery.trim() && showUserSearch) {
      searchUsers();
    } else {
      setSearchResults([]);
    }
  }, [searchQuery, showUserSearch]);

  const loadChats = async () => {
    try {
      const response = await apiHelpers.getUserChats();
      if (response.data.success) {
        setChats(response.data.data.chats);
      }
    } catch (error) {
      console.error('Load chats error:', error);
      toast.error('Failed to load chats');
    } finally {
      setLoading(false);
    }
  };

  const searchUsers = async () => {
    try {
      setSearchLoading(true);
      const response = await apiHelpers.searchUsers(searchQuery);
      if (response.data.success) {
        setSearchResults(response.data.data.users);
      }
    } catch (error) {
      console.error('Search users error:', error);
      toast.error('Failed to search users');
    } finally {
      setSearchLoading(false);
    }
  };

  const startChat = async (userId) => {
    try {
      console.log('Starting chat with user:', userId);
      const response = await apiHelpers.createPrivateChat(userId);
      if (response.data.success) {
        const chatId = response.data.data.chat._id;
        console.log('Chat created/found:', chatId);
        navigate(`/chat/${chatId}`);
      }
    } catch (error) {
      console.error('Start chat error:', error);
      toast.error('Failed to start chat. Please try again.');
    }
  };

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  const formatLastMessageTime = (timestamp) => {
    if (!timestamp) return '';
    const messageTime = moment(timestamp);
    const now = moment();
    
    if (now.diff(messageTime, 'minutes') < 1) {
      return 'Just now';
    } else if (now.diff(messageTime, 'hours') < 1) {
      return `${now.diff(messageTime, 'minutes')}m ago`;
    } else if (now.diff(messageTime, 'days') < 1) {
      return `${now.diff(messageTime, 'hours')}h ago`;
    } else if (now.diff(messageTime, 'days') < 7) {
      return `${now.diff(messageTime, 'days')}d ago`;
    } else {
      return messageTime.format('MMM DD');
    }
  };

  const truncateMessage = (message, maxLength = 50) => {
    if (!message) return '';
    if (message.length <= maxLength) return message;
    return message.substring(0, maxLength) + '...';
  };

  if (loading) {
    return (
      <div className="min-h-screen flex items-center justify-center bg-gray-50">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-600 mx-auto mb-4"></div>
          <p className="text-gray-600">Loading your chats...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-50">
      {/* Header */}
      <header className="bg-white shadow-sm border-b border-gray-200">
        <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
          <div className="flex justify-between items-center py-4">
            <div className="flex items-center">
              <div className="flex-shrink-0">
                <FaComments className="h-8 w-8 text-blue-600" />
              </div>
              <div className="ml-3">
                <h1 className="text-xl font-semibold text-gray-900">Chat Dashboard</h1>
                <p className="text-sm text-gray-600">
                  {connected ? (
                    <span className="flex items-center">
                      <FaCircle className="h-2 w-2 text-green-500 mr-1" />
                      Connected
                    </span>
                  ) : (
                    <span className="flex items-center">
                      <FaCircle className="h-2 w-2 text-red-500 mr-1" />
                      Disconnected
                    </span>
                  )}
                </p>
              </div>
            </div>
            
            <div className="flex items-center space-x-4">
              <div className="text-sm text-gray-600">
                <span className="font-medium">{onlineUsers.length}</span> users online
              </div>
              <div className="relative">
                <button
                  onClick={() => setShowProfile(!showProfile)}
                  className="flex items-center space-x-2 text-gray-600 hover:text-gray-900 transition-colors"
                >
                  <FaUser className="h-5 w-5" />
                  <span className="font-medium">{user?.name}</span>
                </button>
                
                {showProfile && (
                  <div className="absolute right-0 mt-2 w-48 bg-white rounded-md shadow-lg py-1 z-10">
                    <div className="px-4 py-2 border-b">
                      <p className="text-sm font-medium text-gray-900">{user?.name}</p>
                      <p className="text-xs text-gray-500">{user?.email}</p>
                    </div>
                    <button
                      onClick={handleLogout}
                      className="w-full text-left px-4 py-2 text-sm text-red-600 hover:bg-red-50 flex items-center"
                    >
                      <FaSignOutAlt className="h-4 w-4 mr-2" />
                      Logout
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>
        </div>
      </header>

      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8 py-8">
        <div className="grid grid-cols-1 lg:grid-cols-3 gap-8">
          {/* Chat List */}
          <div className="lg:col-span-2">
            <div className="bg-white rounded-lg shadow-sm border border-gray-200">
              <div className="p-6 border-b border-gray-200">
                <div className="flex items-center justify-between mb-4">
                  <h2 className="text-lg font-semibold text-gray-900">Your Chats</h2>
                  <button
                    onClick={() => setShowUserSearch(!showUserSearch)}
                    className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors flex items-center space-x-2"
                  >
                    <FaPlus className="h-4 w-4" />
                    <span>New Chat</span>
                  </button>
                </div>

                {/* Search for new chats */}
                {showUserSearch && (
                  <div className="mb-4">
                    <div className="relative">
                      <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                      <input
                        type="text"
                        placeholder="Search users to start a chat..."
                        value={searchQuery}
                        onChange={(e) => setSearchQuery(e.target.value)}
                        className="w-full pl-10 pr-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                      />
                    </div>
                    
                    {/* Search Results */}
                    {searchQuery && (
                      <div className="mt-2 bg-gray-50 rounded-lg p-2 max-h-40 overflow-y-auto">
                        {searchLoading ? (
                          <div className="text-center py-2">
                            <div className="animate-spin rounded-full h-4 w-4 border-b-2 border-blue-600 mx-auto"></div>
                          </div>
                        ) : searchResults.length > 0 ? (
                          searchResults.map((searchUser) => (
                            <div
                              key={searchUser._id}
                              className="flex items-center justify-between p-2 hover:bg-white rounded cursor-pointer"
                              onClick={() => startChat(searchUser._id)}
                            >
                              <div className="flex items-center space-x-3">
                                <div className="relative">
                                  <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                                    <FaUser className="h-4 w-4 text-gray-600" />
                                  </div>
                                  {isUserOnline(searchUser._id) && (
                                    <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                                  )}
                                </div>
                                <div>
                                  <p className="text-sm font-medium text-gray-900">{searchUser.name}</p>
                                  <p className="text-xs text-gray-500">{searchUser.email}</p>
                                </div>
                              </div>
                              <button className="text-blue-600 hover:text-blue-700 text-sm font-medium">
                                Start Chat
                              </button>
                            </div>
                          ))
                        ) : (
                          <p className="text-sm text-gray-500 text-center py-2">No users found</p>
                        )}
                      </div>
                    )}
                  </div>
                )}
              </div>

              {/* Chat List */}
              <div className="divide-y divide-gray-200">
                {chats.length > 0 ? (
                  chats.map((chat) => {
                    const otherParticipant = chat.otherParticipants[0];
                    const isOnline = otherParticipant && isUserOnline(otherParticipant._id);
                    
                    return (
                      <div
                        key={chat._id}
                        className="p-4 hover:bg-gray-50 cursor-pointer transition-colors"
                        onClick={() => navigate(`/chat/${chat._id}`)}
                      >
                        <div className="flex items-center space-x-3">
                          <div className="relative">
                            <div className="w-12 h-12 bg-gray-300 rounded-full flex items-center justify-center">
                              <FaUser className="h-6 w-6 text-gray-600" />
                            </div>
                            {isOnline && (
                              <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-white"></div>
                            )}
                          </div>
                          
                          <div className="flex-1 min-w-0">
                            <div className="flex items-center justify-between">
                              <h3 className="text-sm font-medium text-gray-900 truncate">
                                {chat.chatName || otherParticipant?.name || 'Unknown User'}
                              </h3>
                              <span className="text-xs text-gray-500">
                                {formatLastMessageTime(chat.lastMessageTime)}
                              </span>
                            </div>
                            <p className="text-sm text-gray-500 truncate mt-1">
                              {chat.lastMessage ? 
                                chat.lastMessage.messageType === 'text' ? 
                                  truncateMessage(chat.lastMessage.content) :
                                  `ðŸ“Ž ${chat.lastMessage.messageType}` :
                                'No messages yet'
                              }
                            </p>
                          </div>
                        </div>
                      </div>
                    );
                  })
                ) : (
                  <div className="p-8 text-center">
                    <FaComments className="h-12 w-12 text-gray-400 mx-auto mb-4" />
                    <h3 className="text-lg font-medium text-gray-900 mb-2">No chats yet</h3>
                    <p className="text-gray-500 mb-4">Start a conversation by searching for users</p>
                    <button
                      onClick={() => setShowUserSearch(true)}
                      className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                    >
                      Find Users
                    </button>
                  </div>
                )}
              </div>
            </div>
          </div>

          {/* Sidebar */}
          <div className="space-y-6">
            {/* Online Users */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
                <FaUsers className="h-5 w-5 mr-2" />
                Online Users ({onlineUsers.length})
              </h3>
              
              <div className="space-y-3 max-h-64 overflow-y-auto">
                {onlineUsers.length > 0 ? (
                  onlineUsers.map((onlineUser) => (
                    <div key={onlineUser.userId} className="flex items-center space-x-3">
                      <div className="relative">
                        <div className="w-8 h-8 bg-gray-300 rounded-full flex items-center justify-center">
                          <FaUser className="h-4 w-4 text-gray-600" />
                        </div>
                        <div className="absolute -bottom-1 -right-1 w-3 h-3 bg-green-500 rounded-full border-2 border-white"></div>
                      </div>
                      <div className="flex-1 min-w-0">
                        <p className="text-sm font-medium text-gray-900 truncate">
                          {onlineUser.userInfo.name}
                        </p>
                        <p className="text-xs text-green-600">Online now</p>
                      </div>
                      {onlineUser.userId !== user._id && (
                        <button
                          onClick={() => startChat(onlineUser.userId)}
                          className="text-blue-600 hover:text-blue-700 text-xs font-medium"
                        >
                          Chat
                        </button>
                      )}
                    </div>
                  ))
                ) : (
                  <p className="text-sm text-gray-500 text-center py-4">No users online</p>
                )}
              </div>
            </div>

            {/* Connection Status */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Connection Status</h3>
              <div className="flex items-center space-x-3">
                <div className={`w-4 h-4 rounded-full ${connected ? 'bg-green-500' : 'bg-red-500'}`}></div>
                <div>
                  <p className="text-sm font-medium text-gray-900">
                    {connected ? 'Connected' : 'Disconnected'}
                  </p>
                  <p className="text-xs text-gray-500">
                    {connected ? 'Real-time messaging active' : 'Reconnecting...'}
                  </p>
                </div>
              </div>
            </div>

            {/* Quick Actions */}
            <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
              <h3 className="text-lg font-semibold text-gray-900 mb-4">Quick Actions</h3>
              <div className="space-y-2">
                <button
                  onClick={() => setShowUserSearch(true)}
                  className="w-full text-left px-3 py-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md transition-colors"
                >
                  ðŸ” Find new users
                </button>
                <button
                  onClick={loadChats}
                  className="w-full text-left px-3 py-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md transition-colors"
                >
                  ðŸ”„ Refresh chats
                </button>
                <button
                  onClick={() => setShowProfile(!showProfile)}
                  className="w-full text-left px-3 py-2 text-sm text-gray-700 hover:bg-gray-100 rounded-md transition-colors"
                >
                  ðŸ‘¤ View profile
                </button>
              </div>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
