import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { FaSearch, FaPlus, FaSignOutAlt, FaUser, FaComments, FaUsers, FaCircle, FaCog, FaBell, FaEllipsisV } from 'react-icons/fa';
import { useAuth } from '../context/AuthContext';
import { useSocket } from '../context/SocketContext';
import { apiHelpers } from '../utils/api';
import { toast } from 'react-hot-toast';
import moment from 'moment';

const Dashboard = () => {
  const [chats, setChats] = useState([]);
  const [searchQuery, setSearchQuery] = useState('');
  const [searchResults, setSearchResults] = useState([]);
  const [loading, setLoading] = useState(true);
  const [searchLoading, setSearchLoading] = useState(false);
  const [showUserSearch, setShowUserSearch] = useState(false);
  const [showProfile, setShowProfile] = useState(false);
  const [activeTab, setActiveTab] = useState('chats');
  
  const { user, logout } = useAuth();
  const { connected, onlineUsers, isUserOnline } = useSocket();
  const navigate = useNavigate();

  // Load user chats on component mount
  useEffect(() => {
    loadChats();
  }, []);

  // Search users when query changes
  useEffect(() => {
    if (searchQuery.trim() && activeTab === 'users') {
      searchUsers();
    } else {
      setSearchResults([]);
    }
  }, [searchQuery, activeTab]);

  const loadChats = async () => {
    try {
      const response = await apiHelpers.getUserChats();
      if (response.data.success) {
        setChats(response.data.data.chats);
      }
    } catch (error) {
      console.error('Load chats error:', error);
      toast.error('Failed to load chats');
    } finally {
      setLoading(false);
    }
  };

  const searchUsers = async () => {
    try {
      setSearchLoading(true);
      const response = await apiHelpers.searchUsers(searchQuery);
      if (response.data.success) {
        setSearchResults(response.data.data.users);
      }
    } catch (error) {
      console.error('Search users error:', error);
      toast.error('Failed to search users');
    } finally {
      setSearchLoading(false);
    }
  };

  const startChat = async (userId) => {
    try {
      console.log('Starting chat with user:', userId);
      const response = await apiHelpers.createPrivateChat(userId);
      if (response.data.success) {
        const chatId = response.data.data.chat._id;
        console.log('Chat created/found:', chatId);
        navigate(`/chat/${chatId}`);
      }
    } catch (error) {
      console.error('Start chat error:', error);
      toast.error('Failed to start chat. Please try again.');
    }
  };

  const handleLogout = async () => {
    await logout();
    navigate('/login');
  };

  const formatLastMessageTime = (timestamp) => {
    if (!timestamp) return '';
    const messageTime = moment(timestamp);
    const now = moment();
    
    if (now.diff(messageTime, 'minutes') < 1) {
      return 'now';
    } else if (now.diff(messageTime, 'hours') < 1) {
      return `${now.diff(messageTime, 'minutes')}m`;
    } else if (now.diff(messageTime, 'days') < 1) {
      return `${now.diff(messageTime, 'hours')}h`;
    } else if (now.diff(messageTime, 'days') < 7) {
      return `${now.diff(messageTime, 'days')}d`;
    } else {
      return messageTime.format('MMM DD');
    }
  };

  const truncateMessage = (message, maxLength = 45) => {
    if (!message) return '';
    if (message.length <= maxLength) return message;
    return message.substring(0, maxLength) + '...';
  };

  if (loading) {
    return (
      <div className="min-h-screen bg-gray-900 flex items-center justify-center">
        <div className="text-center">
          <div className="animate-spin rounded-full h-12 w-12 border-b-2 border-blue-500 mx-auto mb-4"></div>
          <p className="text-gray-400">Loading your chats...</p>
        </div>
      </div>
    );
  }

  return (
    <div className="min-h-screen bg-gray-900 text-white">
      <div className="flex h-screen">
        {/* Left Sidebar - Navigation */}
        <div className="w-20 bg-gray-800 flex flex-col items-center py-4 space-y-4">
          <div className="w-12 h-12 bg-gradient-to-r from-blue-500 to-purple-600 rounded-2xl flex items-center justify-center mb-4">
            <FaComments className="h-6 w-6 text-white" />
          </div>
          
          <button
            onClick={() => setActiveTab('chats')}
            className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all ${
              activeTab === 'chats' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-700 text-gray-400 hover:bg-gray-600 hover:text-white'
            }`}
            title="Chats"
          >
            <FaComments className="h-5 w-5" />
          </button>
          
          <button
            onClick={() => setActiveTab('users')}
            className={`w-12 h-12 rounded-2xl flex items-center justify-center transition-all ${
              activeTab === 'users' 
                ? 'bg-blue-600 text-white' 
                : 'bg-gray-700 text-gray-400 hover:bg-gray-600 hover:text-white'
            }`}
            title="Users"
          >
            <FaUsers className="h-5 w-5" />
          </button>
          
          <div className="flex-1"></div>
          
          <button
            onClick={() => setShowProfile(!showProfile)}
            className="w-12 h-12 bg-gray-700 rounded-2xl flex items-center justify-center hover:bg-gray-600 transition-colors relative"
            title="Profile"
          >
            <FaUser className="h-5 w-5 text-gray-400" />
            <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-gray-800"></div>
          </button>
        </div>

        {/* Main Content */}
        <div className="flex-1 flex">
          {/* Chat List */}
          <div className="w-80 bg-gray-800 border-r border-gray-700">
            {/* Header */}
            <div className="p-4 border-b border-gray-700">
              <div className="flex items-center justify-between mb-4">
                <h1 className="text-xl font-semibold text-white">
                  {activeTab === 'chats' ? 'Messages' : 'Find People'}
                </h1>
                <button
                  onClick={() => setActiveTab('users')}
                  className="w-8 h-8 bg-blue-600 rounded-lg flex items-center justify-center hover:bg-blue-700 transition-colors"
                  title="New Chat"
                >
                  <FaPlus className="h-4 w-4 text-white" />
                </button>
              </div>

              {/* Search Bar */}
              <div className="relative">
                <FaSearch className="absolute left-3 top-1/2 transform -translate-y-1/2 text-gray-400 h-4 w-4" />
                <input
                  type="text"
                  placeholder={activeTab === 'chats' ? "Search conversations..." : "Search users..."}
                  value={searchQuery}
                  onChange={(e) => setSearchQuery(e.target.value)}
                  className="w-full pl-10 pr-4 py-2 bg-gray-700 border border-gray-600 rounded-lg text-white placeholder-gray-400 focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
            </div>

            {/* Chat List / User Search Results */}
            <div className="overflow-y-auto h-full pb-20">
              {activeTab === 'chats' ? (
                /* Chat List */
                <div className="p-2">
                  {chats.length > 0 ? (
                    chats
                      .filter(chat => {
                        if (!searchQuery) return true;
                        const otherParticipant = chat.otherParticipants[0];
                        return otherParticipant?.name?.toLowerCase().includes(searchQuery.toLowerCase()) ||
                               chat.lastMessage?.content?.toLowerCase().includes(searchQuery.toLowerCase());
                      })
                      .map((chat) => {
                        const otherParticipant = chat.otherParticipants[0];
                        const isOnline = otherParticipant && isUserOnline(otherParticipant._id);
                        
                        return (
                          <div
                            key={chat._id}
                            className="p-3 hover:bg-gray-700 cursor-pointer transition-colors rounded-lg mb-1 group"
                            onClick={() => navigate(`/chat/${chat._id}`)}
                          >
                            <div className="flex items-center space-x-3">
                              <div className="relative">
                                <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center text-white font-semibold">
                                  {otherParticipant?.name?.charAt(0)?.toUpperCase() || '?'}
                                </div>
                                {isOnline && (
                                  <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-gray-800"></div>
                                )}
                              </div>
                              
                              <div className="flex-1 min-w-0">
                                <div className="flex items-center justify-between">
                                  <h3 className="text-sm font-medium text-white truncate">
                                    {chat.chatName || otherParticipant?.name || 'Unknown User'}
                                  </h3>
                                  <span className="text-xs text-gray-400">
                                    {formatLastMessageTime(chat.lastMessageTime)}
                                  </span>
                                </div>
                                <p className="text-sm text-gray-400 truncate mt-1">
                                  {chat.lastMessage ? 
                                    chat.lastMessage.messageType === 'text' ? 
                                      truncateMessage(chat.lastMessage.content) :
                                      `📎 ${chat.lastMessage.messageType}` :
                                    'No messages yet'
                                  }
                                </p>
                              </div>
                            </div>
                          </div>
                        );
                      })
                  ) : (
                    <div className="p-8 text-center">
                      <div className="w-16 h-16 bg-gray-700 rounded-full flex items-center justify-center mx-auto mb-4">
                        <FaComments className="h-8 w-8 text-gray-500" />
                      </div>
                      <h3 className="text-lg font-medium text-gray-300 mb-2">No conversations yet</h3>
                      <p className="text-gray-500 mb-4">Start chatting by finding users</p>
                      <button
                        onClick={() => setActiveTab('users')}
                        className="bg-blue-600 text-white px-4 py-2 rounded-lg hover:bg-blue-700 transition-colors"
                      >
                        Find People
                      </button>
                    </div>
                  )}
                </div>
              ) : (
                /* User Search Results */
                <div className="p-2">
                  {searchQuery && searchResults.length > 0 ? (
                    searchResults.map((searchUser) => (
                      <div
                        key={searchUser._id}
                        className="p-3 hover:bg-gray-700 cursor-pointer transition-colors rounded-lg mb-1 group"
                        onClick={() => startChat(searchUser._id)}
                      >
                        <div className="flex items-center space-x-3">
                          <div className="relative">
                            <div className="w-12 h-12 bg-gradient-to-r from-green-500 to-blue-500 rounded-full flex items-center justify-center text-white font-semibold">
                              {searchUser.name.charAt(0).toUpperCase()}
                            </div>
                            {isUserOnline(searchUser._id) && (
                              <div className="absolute -bottom-1 -right-1 w-4 h-4 bg-green-500 rounded-full border-2 border-gray-800"></div>
                            )}
                          </div>
                          <div className="flex-1">
                            <h3 className="text-sm font-medium text-white">{searchUser.name}</h3>
                            <p className="text-xs text-gray-400">{searchUser.email}</p>
                          </div>
                          <button className="opacity-0 group-hover:opacity-100 bg-blue-600 text-white px-3 py-1 rounded text-xs hover:bg-blue-700 transition-all">
                            Chat
                          </button>
                        </div>
                      </div>
                    ))
                  ) : searchQuery && !searchLoading ? (
                    <div className="p-8 text-center">
                      <p className="text-gray-500">No users found for "{searchQuery}"</p>
                    </div>
                  ) : !searchQuery ? (
                    <div className="p-8 text-center">
                      <div className="w-16 h-16 bg-gray-700 rounded-full flex items-center justify-center mx-auto mb-4">
                        <FaUsers className="h-8 w-8 text-gray-500" />
                      </div>
                      <h3 className="text-lg font-medium text-gray-300 mb-2">Find People</h3>
                      <p className="text-gray-500">Search for users to start chatting</p>
                    </div>
                  ) : null}
                  
                  {searchLoading && (
                    <div className="p-4 text-center">
                      <div className="animate-spin rounded-full h-6 w-6 border-b-2 border-blue-500 mx-auto"></div>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>

          {/* Right Panel - Welcome/Status */}
          <div className="flex-1 flex items-center justify-center bg-gray-850">
            <div className="text-center max-w-md">
              <div className="w-24 h-24 bg-gradient-to-r from-blue-500 to-purple-600 rounded-full flex items-center justify-center mx-auto mb-6">
                <FaComments className="h-12 w-12 text-white" />
              </div>
              <h2 className="text-2xl font-bold text-white mb-2">Welcome to Chat App</h2>
              <p className="text-gray-400 mb-6">Select a conversation to start messaging</p>
              
              <div className="bg-gray-800 rounded-lg p-4 mb-6">
                <div className="flex items-center justify-center space-x-2 mb-2">
                  <div className={`w-3 h-3 rounded-full ${connected ? 'bg-green-500' : 'bg-red-500'}`}></div>
                  <span className="text-sm text-gray-300">
                    {connected ? 'Connected' : 'Disconnected'}
                  </span>
                </div>
                <p className="text-xs text-gray-500">
                  {onlineUsers.length} users online
                </p>
              </div>

              <button
                onClick={() => setActiveTab('users')}
                className="bg-blue-600 text-white px-6 py-3 rounded-lg hover:bg-blue-700 transition-colors inline-flex items-center space-x-2"
              >
                <FaPlus className="h-4 w-4" />
                <span>Start New Chat</span>
              </button>
            </div>
          </div>
        </div>
      </div>

      {/* Profile Dropdown */}
      {showProfile && (
        <div className="fixed bottom-24 left-4 w-64 bg-gray-800 rounded-lg shadow-2xl border border-gray-700 z-50">
          <div className="p-4 border-b border-gray-700">
            <div className="flex items-center space-x-3">
              <div className="w-12 h-12 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full flex items-center justify-center text-white font-semibold">
                {user?.name?.charAt(0)?.toUpperCase()}
              </div>
              <div>
                <p className="text-sm font-medium text-white">{user?.name}</p>
                <p className="text-xs text-gray-400">{user?.email}</p>
              </div>
            </div>
          </div>
          
          <div className="p-2">
            <button className="w-full text-left px-3 py-2 text-sm text-gray-300 hover:bg-gray-700 rounded flex items-center space-x-2">
              <FaCog className="h-4 w-4" />
              <span>Settings</span>
            </button>
            <button
              onClick={handleLogout}
              className="w-full text-left px-3 py-2 text-sm text-red-400 hover:bg-gray-700 rounded flex items-center space-x-2"
            >
              <FaSignOutAlt className="h-4 w-4" />
              <span>Logout</span>
            </button>
          </div>
        </div>
      )}
    </div>
  );
};

export default Dashboard;
